mapper.select:
SELECT role_id, access_id FROM t_roleaccesslin WHERE 1 = 1
AND role_id = #{role_id}
AND access_id = #{access_id}

mapper.delete:
DELETE
FROM
t_roleaccesslin
WHERE 1 = 1

    AND role_id = #{role_id}


    AND access_id = #{access_id}

mapper.select:
SELECT m.access_id AS "key", m.access_name AS "title", CASE WHEN rm.id IS NULL THEN 'left' ELSE 'right' END AS "direction" FROM (SELECT ac.access_id, ac.access_name, ac.access_order, mo.module_order FROM t_accessmst ac,t_modulemst mo WHERE 1 = 1 AND ac.module_id = mo.module_id AND mo.module_type = '2' AND ac.is_delete = '0' AND ac.is_delete = '0' AND mo.is_delete = '0' AND mo.valid_sta = '0'
#{item}
) m LEFT JOIN (SELECT id, access_id FROM t_roleaccesslin WHERE role_id = #{roleId}) rm ON m.access_id = rm.access_id ORDER BY m.module_order ASC,access_order ASC
