mapper.select:
SELECT role_id, menu_id FROM t_rolemenulin WHERE 1 = 1
AND role_id = #{role_id}
AND menu_id = #{menu_id}

mapper.delete:
DELETE FROM
t_rolemenulin
WHERE 1 = 1

    AND role_id = #{role_id}


    AND menu_id = #{menu_id}

mapper.select:
SELECT m.menu_id AS "key", m.menu_name AS "title", CASE WHEN rm.id IS NULL THEN 'left' ELSE 'right' END AS "direction" FROM (SELECT me.menu_id, me.menu_name, me.menu_order, mo.module_order FROM t_menumst me,t_modulemst mo WHERE 1 = 1 AND me.module_id = mo.module_id AND mo.module_type = '0' AND me.is_delete = '0' AND me.is_delete = '0' AND mo.is_delete = '0' AND mo.valid_sta = '0'
#{item}
) m LEFT JOIN (SELECT id, menu_id FROM t_rolemenulin WHERE role_id = #{roleId}) rm ON m.menu_id = rm.menu_id ORDER BY m.module_order ASC,menu_order ASC

SELECT id, operate_code FROM t_rolemenulin WHERE 1 = 1
AND role_id = #{role_id}
AND menu_id = #{menu_id}

update.foreach:
UPDATE t_rolemenulin

    operate_code = #{item.operate_code},
    update_id = #{item.update_id},
    update_time = #{item.update_time}

WHERE id = #{item.id}
