mapper.select:
SELECT role_id, route_id FROM t_roleroutelin WHERE 1 = 1
AND role_id = #{role_id}
AND route_id = #{route_id}

mapper.delete:
DELETE
FROM
t_roleroutelin
WHERE 1 = 1

    AND role_id = #{role_id}


    AND route_id = #{route_id}

mapper.select:
SELECT m.route_id AS "key", m.route_name AS "title", CASE WHEN rm.id IS NULL THEN 'left' ELSE 'right' END AS "direction" FROM (SELECT ro.route_id, ro.route_name, ro.route_order, mo.module_order FROM t_routemst ro,t_modulemst mo WHERE 1 = 1 AND ro.module_id = mo.module_id AND mo.module_type = '1' AND ro.is_delete = '0' AND ro.is_delete = '0' AND mo.is_delete = '0' AND mo.valid_sta = '0'
#{item}
) m LEFT JOIN (SELECT id, route_id FROM t_roleroutelin WHERE role_id = #{roleId}) rm ON m.route_id = rm.route_id ORDER BY m.module_order ASC,route_order ASC
